var $=document.querySelector.bind(document);
var $$=document.querySelectorAll.bind(document);

var playList=$('.playList');
var playerTitle=$('.player-title')
var cdImg=$('.player-cd-img')
var audio=$('#audio')
var playBtn=$('.btn-play');
var progress=$('#progress');
var curTime=$('.player-time-current')
var durTime=$('.player-time-duration')
var repeatBtn=$('.player-control-repeat')
var nextBtn=$('.player-control-forward');
var prevBtn=$('.player-control-backward');
var randomBtn=$('.player-control-random');
var playCD=$('.player-cd')
var playListItem=$('.playList-item');

var app={
    currentIndex: 0,
    //list bài hát
    songs:[
        {
            name:'Chàng Trai Của Em Remix',
            singer:'Duy Văn Phạm',
            path:'https://nhacchuong123.com/nhac-chuong/nhac-hay/Th%E1%BA%BF%20Gi%E1%BB%9Bi%20%E1%BA%A2o%20T%C3%ACnh%20Y%C3%AAu%20Th%E1%BA%ADt%20Remix%20-%20Tr%E1%BB%8Bnh%20%C4%90%C3%ACnh%20Quang%20ft.%20DJ%20LeeK54.mp3',
             image:'data:image/jpeg;base64,'
        },
        {
            name:'Kết Duyên Remix',
            singer:'YuniBoo',
            path:'http://api.mp3.zing.vn/api/streaming/audio/ZWB0977A/320',
            // image:'data:image/jpeg;base64,'
        },
        {
            name:'Thế giới ảo tình yêu thật',
            singer:'Trịnh Đình Quang',
            path:'http://api.mp3.zing.vn/api/streaming/audio/ZZCIUIWZ/320',
            // image:'data:image/jpeg;base64,'
        },
        {
            name:'Đường quyền tình yêu',
            singer:'Phạm Nguyên Ngọc, VAnh',
            path:'http://api.mp3.zing.vn/api/streaming/audio/ZZB8D9C8/320',
            image:'data:image/jpeg;base64,'
        },
        {
            name:'Chân Ngắn',
            singer:'Cẩm Vân Phạm',
            path:'http://api.mp3.zing.vn/api/streaming/audio/ZW6ADAZI/320',
            image:'data:image/jpeg;base64,'
        },
    ],

    //render list bài hát
    render:function(){
        var _this = this; 
        var htmls=this.songs.map(function(song,index){
            return `
            <div class="playList-item ${_this.currentIndex===index?'playing':''}" data-index=${index} >
                <img class="playList-item-img" src="${song.image}" alt="">
                <div class="playList-item-content">
                    <p class="playList-item-content-title">${song.name}</p>
                    <p class="playList-item-content-singer">${song.singer}</p>
                </div>
                <div class="playList-item-icon">
                    <i class="fa-solid fa-ellipsis"></i>
                </div>
            </div>
            `
        })
        playList.innerHTML= htmls.join('\n')
    },

    //lấy ra bài hát hiện tại
    defineProperties:function(){
        var _this = this
        Object.defineProperty(this, 'currentSong', {
            get:function(){
                return this.currentSong=_this.songs[_this.currentIndex];
            }
        })
    },

    //load bài hát hiện tại
    loadCurrentSong:function(){
        playerTitle.innerText=this.currentSong.name;
        cdImg.style.backgroundImage=`url(${this.currentSong.image})`;
        audio.src=this.currentSong.path;
    },

    //time format
    timeFormat:function(time){//nhận vào tổng số giây
        var min=Math.floor(time/60);
        var sec=Math.floor(time%60);
        return (`${min}`>=10? `${min}`:'0'+`${min}`)+':'+(`${sec}`>=10? `${sec}`:'0'+`${sec}`)
    },

    //next Song
    nextSong:function(){
        this.currentIndex++;
        if(this.currentIndex>=this.songs.length){
            this.currentIndex=0;
        }
        this.loadCurrentSong();
        audio.play();
        this.render();

    },

     //prev Song
     prevSong:function(){
        this.currentIndex--;
        if(this.currentIndex<0){
            this.currentIndex=this.songs.length-1;
        }
        this.loadCurrentSong();
        audio.play();
        this.render();
    },


    //random song
    randomSong:function(){
        do{
            var newIndex=Math.floor(Math.random()*this.songs.length);
        }while(this.currentIndex===newIndex);
        this.currentIndex=newIndex;
        this.loadCurrentSong();
        this.render();
        audio.play();
    },

    //kéo đến đâu thì active đến đó
    scrollToActiveSong:function(){
        $('.playList-item.playing').scrollIntoView({
            behavior: 'smooth',block:'end',inline:'nearest'
        })
    },

    //xử lý sự kiện
    handleEvent:function(){
        var _this=this;
            
        //click để play/pause
        playBtn.onclick=function(){
            if(playBtn.classList.contains('btn-play-active')){
                playBtn.classList.remove('btn-play-active');
                audio.pause();
                cdRotate.pause();
            
            }else{
                playBtn.classList.add('btn-play-active');
                audio.play();
                cdRotate.play();
            }
        }

        //khi tiến độ bài hát thay đổi
        audio.ontimeupdate=function(){
            if(audio.duration){//nếu nó k phải NAN
                progress.value=audio.currentTime/audio.duration *100;
            }
            curTime.innerText=_this.timeFormat(audio.currentTime);
        }

        //khi tua bài hát
        progress.oninput=function(){
            audio.currentTime=progress.value/100 * audio.duration;
        }

        //khi load bài hát
        audio.onloadedmetadata=function(){
            curTime.innerText=_this.timeFormat(audio.currentTime);
            durTime.innerText=_this.timeFormat(audio.duration);
        }

        //next song
        nextBtn.onclick=function(){
            if(randomBtn.classList.contains('active')){
                _this.randomSong();
                cdRotate.play();
            }else{
                _this.nextSong();
                cdRotate.play();
            }
            playBtn.classList.add('btn-play-active')
            _this.scrollToActiveSong();
        }

        //prev song
        prevBtn.onclick=function(){
            if(randomBtn.classList.contains('active')){
                _this.randomSong();
                cdRotate.play();
            }else{
                _this.prevSong();
                cdRotate.play();
            }
            playBtn.classList.add('btn-play-active')
            _this.scrollToActiveSong();
        }

        //khi kết thúc bài hát thì chuyển bài tiếp theo
        audio.onended=function(){
            if(repeatBtn.classList.contains('active')){//hát lại
                audio.play();
                cdRotate.play();
            }else if(randomBtn.classList.contains('active')){
                _this.randomSong();
                cdRotate.play();
            }
            else{
                _this.nextSong();
                cdRotate.play();
            }
            _this.scrollToActiveSong();
            
        }

        //hát lại bài hát
        repeatBtn.onclick=function(){
            if(repeatBtn.classList.contains('active')){
                repeatBtn.classList.remove('active');
            }else{
                repeatBtn.classList.add('active');
            }
        }

        //phát ngẫu nhiên
        randomBtn.onclick=function(){
            if(randomBtn.classList.contains('active')){
                randomBtn.classList.remove('active');
            }else{
                randomBtn.classList.add('active');
            }
        }

        //cho cd quay
        var cdRotate=cdImg.animate(
            {transform:'rotate(360deg)'},
            {
                duration:10000,
                iterations:Infinity
            }
        )
        cdRotate.pause();

        //khi kéo lại thì cd nhỏ dần
        var cdWidth=playCD.offsetWidth;
        document.onscroll=function(){
            var scrollY=window.scrollY;
            var newCdWidth=cdWidth-scrollY;
            playCD.style.width=newCdWidth>0?newCdWidth+'px':0;
            playCD.style.opacity=newCdWidth/cdWidth;
        }

        //click vô bài nào thì hát bài đó
        playList.onclick=function(e){
           if(e.target.closest('.playList-item:not(.playing)')){
                _this.currentIndex=Number.parseInt(e.target.closest('.playList-item:not(.playing)').getAttribute('data-index'));
                _this.loadCurrentSong();
                _this.render();
                audio.play();
                playBtn.classList.add('btn-play-active')
                cdRotate.play();
            } 
        }
    },

    start:function(){
        this.render();
        this.defineProperties();
        this.loadCurrentSong();
        this.handleEvent();
    }
}
app.start();